sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // construct a GlideRecord to find all the overdue NeedIt tasks, the NeedIt task state can not              be closed
			var rightNow = new GlideDateTime();
			var needItTasks = new GlideRecord('x_58872_needit_needit_task');
			needItTasks.addQuery('due_date','<=',rightNow);
			needItTasks.addQuery('state','!=',3);
			needItTasks.addQuery('assigned_to',current.assigned_to);
			needItTasks.query();

	/*Modify the CountOverdueNeedItTasksAssignedTo Notification Email Script so it doesn't print a message if the Assigned to has only 1 overdue NeedIt Task record.*/
	if ( needItTasks.getRowCount() > 1){
				template.print("In addition to the overdue NeedIt tasks you have " + (needItTasks.getRowCount() - 1 ) + " more overdue tasks.");	
	}



})(current, template, email, email_action, event);
