sysauto_script.script:
var rightNow = new GlideDateTime();
var waitDays = gs.getProperty('x_58872_needit.autoCloseOverdue');
var needItTaskRecords = new GlideRecord('x_58872_needit_needit_task');
needItTaskRecords.addQuery('due_date','<=',(gs.daysAgo(waitDays)));
needItTaskRecords.addActiveQuery('state','!=',3);
needItTaskRecords.query();
while(needItTaskRecords.next()){
gs.info("Auto Closed the NeedIt task Record :"+needItTaskRecords.number);
needItTaskRecords.work_notes = "Record closed because it was too far past the Due date.";
needItTaskRecords.state = 3;
needItTaskRecords.update();
}
